// Configuration

hexo.config.xvideos = Object.assign({
  width: hexo.config.xvideos.width,
  height: hexo.config.xvideos.height,
  url: hexo.config.xvideos.url
}, hexo.config.xvideos);

hexo.extend.tag.register('xvideos', (args) => {
  let url = args[0];
  let width = args[1] || hexo.config.xvideos.width;
  let height = args[2] || hexo.config.xvideos.height;

  let tag = `<iframe src=${url} width=${width} height=${height} scrolling="no" border="0" frameborder="no" framespacing="0" allowfullscreen="true"></iframe>`;
  return tag;

}, { async: true });
